// In App.js in a new project

import React, { useState } from 'react';
import { View, Text, Button, StyleSheet, KeyboardAvoidingView, Platform, TextInput, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import Task from './components/task';

function App() {
  const [task, setTask] = useState();
   const handleAddTask = () => {
     console.log(task)
   }

  return (
    <NavigationContainer>
      <Stack.Navigator 
        initialRouteName={"Home"}
        screenOptions = {{ 
          headerTitleAlign: "center",
          headerTintColor: 'grey',
          headerStyle: {
           backgroundColor: 'lightblue', 
          },
          headerRight: () => <Button title='Edit'
          />
        }}
        >
        <Stack.Screen
          name="Personal" 
          component={PersonalScreen} 
          options= {{  
            title:'Personal',
          }}
          />
        <Stack.Screen 
          name="Home" 
          component={HomeScreen}
          options= {{  
            title:'Welcome, to your Tasks.',
          }}
         />
        <Stack.Screen 
          name="Work" 
          component={WorkScreen} 
          options= {{  
            title:'Work',
          }}
          />
      <Stack.Screen 
        name="School" 
        component={SchoolScreen} 
        options= {{  
          title:'School',
        }}
        />
      <Stack.Screen 
        name="Groceries" 
        component={GroceriesScreen} 
        options= {{  
          title:'Groceries',
        }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}


const HomeScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <Text>Home</Text>
      <Button 
      title="Personal To-Do" 
      onPress={()=> navigation.navigate('Personal')}
      />
      <Button 
      title="Work To-Do" 
      onPress={()=> navigation.navigate('Work')}
      />
      <Button 
      title="School To-Do" 
      onPress={()=> navigation.navigate('School')}
      />
      <Button 
      title="Groceries List" 
      onPress={()=> navigation.navigate('Groceries')}
      />
    </View>
  );
}

const PersonalScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <Text>Personal taks</Text>

    <View style={styles.items}>
      <Task text={'Do Laundry'} />
      <Task text={'Organize Shelf'} />
      <Task text={'Find airpods'} />
      </View>

    <KeyboardAvoidingView
      behavior={Platform.ios === "ios" ? "padding" : "height"}
      style={styles.writeTaskWrapper}
      >
       <TextInput style={styles.input} placeholder={'Write a task'} value={task} onChangeText={text => setTask(text)} />
        <TouchableOpacity onPress={() => handleAddTask()}>

      </TouchableOpacity>
      <View style={styles.addwrapper}>
        <Text style={styles.addText}> + </Text>
         </View>
      </KeyboardAvoidingView>
  </View>
  )
};

const WorkScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
    <Text> Work tasssks </Text>
    <View style={styles.items}>
      <Task text={'Fix Timesheet'} />
      <Task text={'Call at least 3 clients'} />
      <Task text={'Be nice to coworkers'} />  
      <Task text={'Reboot the computers'} /> 
      </View>

    <KeyboardAvoidingView
      behavior={Platform.ios === "ios" ? "padding" : "height"}
      style={styles.writeTaskWrapper}
      >
      <TextInput style={styles.input} placeholder={"Write a new task"} />
      <TouchableOpacity >

      </TouchableOpacity>
      <View style={styles.addwrapper}>
        <Text style={styles.addText}> + </Text>
         </View>
      </KeyboardAvoidingView>
    <Button
      title="Back"
      onPress={() => navigation.goBack()}
      >
      </Button>
  </View>
  )
};

const SchoolScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
    <Text>School taks</Text>
    <View style={styles.items}>
      <Task text={'Learn React Native'} />
      <Task text={'Learn React Navigation'} /> 
      <Task text={'Film Papers Due'} />
      <Task text={'???'} /> 
      <Task text={'Try to graduate'} />  
      </View>

    <KeyboardAvoidingView
      behavior={Platform.ios === "ios" ? "padding" : "height"}
      style={styles.writeTaskWrapper}
      >
      <TextInput style={styles.input} placeholder={"Write a new task"} />
      <TouchableOpacity >

      </TouchableOpacity>
      <View style={styles.addwrapper}>
        <Text style={styles.addText}> + </Text>
         </View>
      </KeyboardAvoidingView>
    <Button
      title="Back"
      onPress={() => navigation.goBack()}
      >
      </Button>
  </View>
  )
};

const GroceriesScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
    <Text>Groceries taks</Text>
    <View style={styles.items}>
      <Task text={'Waffles'} />
      <Task text={'Eggs'} />
      <Task text={'Cake mix'} />
      <Task text={'Taco Seasoning'} />  
      </View>

    <KeyboardAvoidingView
      behavior={Platform.ios === "ios" ? "padding" : "height"}
      style={styles.writeTaskWrapper}
      >
      <TextInput style={styles.input} placeholder={"Write a new task"} />
      <TouchableOpacity >

      </TouchableOpacity>
      <View style={styles.addwrapper}>
        <Text style={styles.addText}> + </Text>
         </View>
      </KeyboardAvoidingView>
    <Button
      title="Back"
      onPress={() => navigation.goBack()}
      >
      </Button>
  </View>
  )
};

const Stack = createStackNavigator();




const styles = StyleSheet.create ({
  container: {  
    flex: 1,
    alignItems: 'center', 
    justifyContent: 'center' 
    },
    taskWrapper: {
      paddingTop: 80,
      paddingHorizontal: 20,
    },
    items: {
      marginTop: 30,
      color: 'blue',
    },
    writeTaskWrapper: {
      position: 'absolute',
      bottom: 60,
      width: '100%',
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
    },
    input: {
      paddingVertical: 15,
      paddingHorizontal: 15,
      backgroundColor: '#FFF',
      borderRadius: 60,
      borderColor: '#C0C0C0',
      borderWidth: 1,
      width: 250,
      alignItems: 'center',
      justifyContent: 'center',
      flex: 1
    },
    addWrapper: {
      width: 60,
      height: 60,
      backgroundColor: '#FFF',
      borderRadius: 60,
      borderColor: '#C0C0C0',
      borderWidth: 1,
    },
    addText: {

    },
});

export default App;
